import React from 'react';
import { ResponsiveContainer, RadialBarChart, RadialBar, PolarAngleAxis } from 'recharts';

const MonthlyChart = ({ data }) => {
    const waterLimit = 1; // Giới hạn nước được cấp (1 - 100%)
    const waterPercentage = data[0].waterUsage / waterLimit; // Phần trăm nước sử dụng
    const dataWithAngle = [{ theta: waterPercentage * 360 }]; // Góc cho kim chỉ

    const label = (
        <text x="50%" y="50%" dominantBaseline="middle" textAnchor="middle" fontSize="40">
            {data[0].waterUsage}/{waterLimit}m<sup>3</sup>
        </text>
    );

    return (
        <ResponsiveContainer width="100%" height={400}>
            <RadialBarChart innerRadius="70%" outerRadius="100%" data={dataWithAngle} startAngle={270} endAngle={-90}>
                <RadialBar
                    background
                    clockWise
                    dataKey="theta"
                    fill="#3f51b5"
                    label={{ position: 'insideStart', fill: '#fff', fontSize: '30' }}
                    isAnimationActive={false}
                />
                <PolarAngleAxis type="number" domain={[-100, 100]} angleAxisId={0} tick={false} />
                <g>{label}</g>
            </RadialBarChart>
        </ResponsiveContainer>
    );
};

export default MonthlyChart;
