import React from 'react';
import { useTable } from 'react-table';

function TableWater(props) {
    const data = React.useMemo(
        () => [
            { month: 'Tháng 1', amount: '100', price: '100,000' },
            { month: 'Tháng 2', amount: '80', price: '80,000' },
            { month: 'Tháng 3', amount: '120', price: '120,000' },
            { month: 'Tháng 4', amount: '150', price: '150,000' },
            { month: 'Tháng 5', amount: '90', price: '90,000' },
            { month: 'Tháng 6', amount: '70', price: '70,000' },
            { month: 'Tháng 7', amount: '110', price: '110,000' },
            { month: 'Tháng 8', amount: '130', price: '130,000' },
            { month: 'Tháng 9', amount: '100', price: '100,000' },
            { month: 'Tháng 10', amount: '80', price: '80,000' },
            { month: 'Tháng 11', amount: '110', price: '110,000' },
            { month: 'Tháng 12', amount: '150', price: '150,000' },
        ],
        [],
    );

    const columns = React.useMemo(
        () => [
            { Header: 'Tháng', accessor: 'month' },
            { Header: 'Số mét khối nước tiêu thụ', accessor: 'amount' },
            { Header: 'Số tiền phải thanh toán', accessor: 'price' },
        ],
        [],
    );

    const tableInstance = useTable({ columns, data });

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

    return (
        <table {...getTableProps()}>
            <thead>
                {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map((cell) => {
                                return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>;
                            })}
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
}

export default TableWater;
